mapper.update:
UPDATE Players
name=#{player_name}, nationality=#{player_nationality}, debut=#{player_debut}, birthday=#{player_birthday},
WHERE player_id = #{player_id};

mapper.select:
SELECT COUNT(*) FROM Players
WHERE

TIMESTAMPDIFF(year, birthday, NOW()) = #{player_debut};

SELECT * FROM Players
WHERE

TIMESTAMPDIFF(year, birthday, NOW()) = #{player_debut};

SELECT COUNT(*) FROM Players
WHERE

TIMESTAMPDIFF(year, birthday, NOW()) < #{player_debut};

SELECT * FROM Players
WHERE

TIMESTAMPDIFF(year, birthday, NOW()) < #{player_debut};

SELECT COUNT(*) FROM Players
WHERE

TIMESTAMPDIFF(year, birthday, NOW()) > #{player_debut};

SELECT * FROM Players
WHERE

TIMESTAMPDIFF(year, birthday, NOW()) > #{player_debut};
